import React ,{useState} from 'react';
import './ExpenseForm.css';


const ExpenseForm =(props) =>{

    const [enteredTitle, setEnteredTitle]= useState('');   //  שימוש 3 פעמים ביוזסטייט יש אפשרות להשתמש בפעם אחת לדוגמא למטה מסומן בכוכבית **
    const [enteredAmount, setEnteredAmount]= useState('');
    const [enteredDate, setEnteredDate]= useState('');
      
   // const [userInput,setUserInput]= useState({
        //enteredTitle:'',
       // enteredAmount:'',
       // enteredDate:'',
  //  });

    const titleChangeHandler = (event)=>{
        setEnteredTitle(event.target.value);

       //  setUserInput({          ***
       //   ...userInput,          ***
       // enteredTitle :event.target.value,  ***

        //setUserInput((prevState)=>{
       // עוד דרך לבצע את ההכנס
           // return{...prevState,enteredTitle:event.target.value};  ***
      //  });
  
    };

    const amountChangeHandler = (event) => {  
        setEnteredAmount(event.target.value);
        //setUserInput({     ***
           //...userInput,    ***
       //  enteredAmount :event.target.value,   ***
      //  });
    };

    const dateChangeHandler = (event) => {
        setEnteredDate(event.target.value);
       // setUserInput({   ***
           // ...userInput, ***
        // enteredDate :event.target.value, ***
       // });
      };

   const sumbitHandler = (event) => {
    event.preventDefault();

    const expenseData = {
        title: enteredTitle,
        amount: enteredAmount,
        date: new Date(enteredDate),
   };

   setEnteredTitle('');
   setEnteredAmount('');
   setEnteredDate('');

   };


return (
 <form onSubmit={sumbitHandler} >
    <div className="new-expense__controls">
     <div className="new-expense__control">
     <label>Title </label>

     <input type='text'
     value={enteredTitle}
      onChange= {titleChangeHandler}/>
     </div>

     <div className="new-expense__control">

      <label>Amount</label>
    <input type='number' min="0.01" step="0.01"
    value={enteredAmount}
     onChange={amountChangeHandler}/>

    </div>

    <div className="expense__controll">
  <label>Date</label>
  <input type='date' min="2019-01-01" max="2022-12-31"
 value={enteredDate}

  onChange={dateChangeHandler} />

     </div>
    </div>

    <div className="new-expense__actions">
      <button type='sumbit'>Add Expense</button>
    </div>
</form>

);
}

export default ExpenseForm
